// Name: Get Tabler Icons
// Description: Copies a Tabler Icon from tabler open-sourced repository.
// Author: Lucas Vogel
// Twitter: @soyvogelino

import "@johnlindquist/kit";

const iconsPath = "https://tabler-icons.io/icons.json";

let files = [];

try {
  let response = await get(iconsPath);
  files = response.data;
} catch (error) {
  notify(error);
