}

for (let filePath of filePaths) {
  let directory = path.dirname(filePath);
  await imagemin([filePath], {
    destination: directory,
    plugins: [
      imageminJpegtran({
        arithmetic: true,
        progressive: true,
      }),
      imageminJpegRecompress({ max: 85 }),
      imageminSvgo(),
      imageminGifsicle({
        optimizationLevel: 2,
      }),
      imageminPngquant({
        quality: [0.3, 0.5],
      }),
    ],
  });
}

