import "@johnlindquist/kit";

// Menu: Speed Test
// Description: Test the speed and performance of your internet connection
// Author: Altrim Beqiri
// Twitter: @altrimbeqiri

const speedTest = await npm("speedtest-net");

interface SpeedTestResult {
  timestamp: Date;
  ping: { jitter: number; latency: number };
  download: { bandwidth: number; bytes: number; elapsed: number };
  upload: { bandwidth: number; bytes: number; elapsed: number };
  packetLoss: number;
  isp: string;
  interface: {
